import Link from 'next/link';
import { useDispatch, useSelector } from 'react-redux';

import { getInputValueAction } from '../reducers/movie'

function MovieInfoLink({ id, src, name }) {
    const dispatch = useDispatch();
    
    const dispatchName = () => {
        dispatch(getInputValueAction(name))
    }

    return (
      <div>
        <Link href="/movieInfo/[id]" as={`/movieInfo/${id}`}>
          <a onClick={dispatchName}>
            <img src={src} style={{ height: '310px', verticalAlign: 'top' }} />
          </a>
        </Link>
      </div>
    )
};

export default MovieInfoLink;
