import React from 'react'
import { useRouter } from 'next/router'
import Slider from "react-slick";
import styled from 'styled-components'

function MovieSlider({ props, movieInfomation }) {
    const router = useRouter()

    const settings = {
        dots: false, 
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 4,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:"wrap-slider",
        initialSlide: 0,
        responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 650,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    const ticketingMovie = () => {
        router.push("/ticketing")
    }

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <div key={item.id} style={{ margin: '0 1rem' }}>
                        <img src={item.poster} style={{ height: '310px', verticalAlign: 'top' }} />
                        <button onClick={ticketingMovie} className="btn ticketing-btn">예매</button>
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieSlider
