import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import styled from 'styled-components';
import { IoHeartOutline, IoHeartSharp } from "react-icons/io5";

import { LIKE_REVIEW_REQUEST, UNLIKE_REVIEW_REQUEST } from '../../reducers/review';

const MovieTalkCardWrap = styled.div`
    width: 500px;
`

function MovieTalkCard({ review }) {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);

    const [LikeAction, setLikeAction] = useState(false);

    const onLike = () => {
        if(me.email) {
            const didDelete = confirm('로그인이 필요합니다. 로그인 화면으로 이동하시겠습니까?');
            if(didDelete) {
                Router.push('/login');
            } else {
                return
            }
        }
        dispatch({
            type: LIKE_REVIEW_REQUEST,
            data: body,
        });
        setLikeAction(true);
    };

    const onUnLike = () => {
        const body = {
            reviewId: review._id,
        }

        dispatch({
            type: UNLIKE_REVIEW_REQUEST,
            data: body,
        });
        setLikeAction(false);
    };


    return (
        <MovieTalkCardWrap>
            <img src={review.iamge} style={{ width: '200px' }} />
            <div>
                <div>작성자 : {review.name}</div>
                <div>{review.rating}</div>
                <div>{review.text}</div>
                {
                    !LikeAction ? 
                    <IoHeartOutline onClick={onLike} style={{ fontSize: '25px' }} />
                    :
                    <IoHeartSharp onClick={onUnLike} style={{ fontSize: '25px' }} />
                }
            </div>
        </MovieTalkCardWrap>
    )
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
      axios.defaults.headers.Cookie = cookie;
    };
    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();
});

export default MovieTalkCard;
