import React from 'react';
import { useSelector } from 'react-redux';
import MyPageLayout from '../components/layout/MyPageLayout';
import styled from 'styled-components';

const UserInfoWrap = styled.div`
    position: absolute;
    top: 225px;
    left: 100px;
`

const UserImage = styled.div`
    width: 150px;
    height: 150px;
    border-radius: 50%;
    background: red;
`

const UserInfo = styled.div`
    width: 700px;
    position: absolute;
    top: 30px;
    left: 200px
`

const NumRelatedUser = styled.div`
    margin-top: 25px;

    & > div {
        display: inline-block;
    }
`

const FollowBtn = styled.div`
    position: absolute;
    top: 0;
    right: 0;
`

function myPage() {
    const { me } = useSelector((state) => state.user);


    return (
        <MyPageLayout>
                <div style={{ height: '300px', background: 'gray', position: 'absolute', top: 0 }}>
                    <UserInfoWrap>
                        <UserImage></UserImage>
                        <UserInfo>
                            <div style={{ fontSize: '2rem' }}>닉네임</div>
                            <NumRelatedUser>
                                <div>follwer 수</div>
                                <div>follwing 수</div>
                                <div>리뷰 수</div>
                            </NumRelatedUser>
                            <FollowBtn>follow 버튼</FollowBtn>
                        </UserInfo>
                    </UserInfoWrap>
                </div>
        </MyPageLayout>
    )
}

export default myPage
