import Link from 'next/link';
import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'

export default function MovieInfo (props) {
    const { result } = props;
    console.log(result);
    const replaceTitle = (title) => {
        return title.replace(/!HS|!HE/g, "");
    };

    const splitPoster = (posters) => {
        if(!posters) {
            posters = "https://res.cloudinary.com/dvmqbovxh/image/upload/v1616866172/noposter_pcwcby.png";
            return posters;
        } else {
            return posters.split("|")[0];
        }
    };

    return (
        <AppLayout>
            <img src={splitPoster(result.posters)} />
            <div>{replaceTitle(result.title)}</div>
            <div>{result.genre}</div>
            <div>{result.rating}</div>
            <div>{result.repRlsDate}</div>
            <div>{result.runtime}분</div>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getServerSideProps(context) {
    const res = await getMovieSeqFromKMDB(context.query.id)
    const result = await res.Data[0].Result[0];

    return {
      props: { result },
    }
}
