import React from 'react'
import Slider from "react-slick";

function MovieInfoSlider({ movieInfomation }) {
    const settings = {
        dots: false, 
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 4,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:"wrap-slider",
        initialSlide: 0,
        responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 650,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    const imageURL = 'https://image.tmdb.org/t/p/w500/'

    const splitPoster = (posterPath) => {
        if(!posterPath) {
            posterPath = "https://res.cloudinary.com/dvmqbovxh/image/upload/v1616866172/noposter_pcwcby.png";
            return posterPath;
        } else {
            return `${imageURL}${item.poster_path}`
        }
    };

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <div key={item.id}>
                        <img src={splitPoster(item.poster_path)} style={{ }} />
                        <div>{item.title}</div>
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieInfoSlider
