import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link'
import styled from 'styled-components'
import { LOG_OUT_REQUEST } from '../reducers/user';

const NavBar = styled.div`
    height: 100vh;
    padding: 3rem 2rem;
    box-sizing: border-box;
    position: relative;
`
const MainMenu = styled.div`
    & > a + a {
        margin-top: 1rem;
    }
`

const UserMenu = styled.div`
    position: absolute;
    bottom: 6rem;

    & > a + a {
        margin-top: 1rem;
    }
`

const HomeLink = styled.a`
    color: red;
    display: block;
    font-size: 2rem;
    cursor: pointer;
`

const MenuLink = styled.a`
    display: block;
    font-size: 1.4rem;
    cursor: pointer;
`

function Nav() {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);

    const handleLogout = () => {
        dispatch({
            type: LOG_OUT_REQUEST,
        })
    }

    if(me) {
        return (
            <NavBar>
                <div className="logo">
                    <Link href="/">
                        <HomeLink>MovieTalk</HomeLink>
                    </Link>
                </div>
                <div>
                    <Link href="/">
                        <MenuLink>홈</MenuLink>
                    </Link>
                    <Link href="/movieSearch">
                        <MenuLink>영화</MenuLink>
                    </Link>
                    <Link href="/movieTalk">
                        <MenuLink>무비톡</MenuLink>
                    </Link>
                </div>
                <div>
                    <Link href="/myPage">
                        <MenuLink>마이페이지</MenuLink>
                    </Link>
                    <MenuLink onClick={handleLogout}>로그아웃</MenuLink>
                </div>
                <div>
                    화이트모드
                </div>
            </NavBar>
        )
    } else {
        return (
            <NavBar>
                <div>
                    <Link href="/">
                        <HomeLink>MovieTalk</HomeLink>
                    </Link>
                </div>
                <MainMenu style={{ marginTop: '3rem' }}>
                    <Link href="/">
                        <MenuLink>홈</MenuLink>
                    </Link>
                    <Link href="/movieSearch">
                        <MenuLink>영화</MenuLink>
                    </Link>
                    <Link href="/movieTalk">
                        <MenuLink>무비톡</MenuLink>
                    </Link>
                </MainMenu>
                <UserMenu>
                    <Link href="/login">
                        <MenuLink>로그인</MenuLink>
                    </Link>
                    <Link href="/signup">
                        <MenuLink>회원가입</MenuLink>
                    </Link>
                </UserMenu>
                <div style={{ position: 'absolute', bottom: '3rem', }}>
                    화이트모드
                </div>
            </NavBar>
        ) 
    }

}

export default Nav
