import { useRouter } from "next/router";

import MovieInfoData from '../../json/movieInfo.json'
import AppLayout from '../../components/AppLayout'

function MovieInfo () {
    const router = useRouter();
    const { id } = router.query;

    const { movieInfomation } = MovieInfoData.movie
    const movieInfoItem = movieInfomation.filter(item => item.id.toString() === id)[0]

    return (
        <AppLayout>
            <div>{movieInfoItem.id}</div>
            <div>{movieInfoItem.name}</div>
            <img src={movieInfoItem.poster} />
        </AppLayout>
    )
}

export default MovieInfo;
