import axios from 'axios';
import { all, fork, call, put, takeLatest } from 'redux-saga/effects';
import {
    LOAD_MOVIE_FROM_TMDB_REQUEST,
    LOAD_MOVIE_FROM_TMDB_SUCCESS,
    LOAD_MOVIE_FROM_TMDB_FAILURE,
} from '../reducers/tmdb';


function loadMovieFromTMDBAPI(data) {
    return axios({
        method: 'get',
        url: `https://api.themoviedb.org/3/search/movie?api_key=2b2e61361a6d20a07e728e56fc296333&query=짱구&language=ko&page=1`,
        withCredentials: true,
    })
}


function* loadMovieFromTMDB(action) {
    try {
        const result = yield call(loadMovieFromTMDBAPI, action.data)
        console.log('saga LOAD_MOVIE_FROM_TMDB');  
        yield put({
            type: LOAD_MOVIE_FROM_TMDB_SUCCESS,
            data: result.data
        })
    } catch(err) {
        yield put({
            type: LOAD_MOVIE_FROM_TMDB_FAILURE,
            data: err.response.data
        })
    }
}


function* watchLoadMovieFromTMDB() {
    yield takeLatest(LOAD_MOVIE_FROM_TMDB_REQUEST, loadMovieFromTMDB);
}


export default function* tmdbSaga() {
    yield all([
      fork(watchLoadMovieFromTMDB),
      
    ]);
}
