import React, { useState, useEffect } from 'react'

import AppLayout from '../components/AppLayout'
import MovieInfoSlider from '../components/MovieInfoSlider';

import { getMovieGenreFromKMDB, getMovieByGenreFromTMDB } from '../api';
import axios from 'axios';

function Home() {
    const [actionGenreMovie, setactionGenreMovie] = useState([]);
    const [comedyGenreMovie, setComedyGenreMovie] = useState([]);
    const [AnimationGenreMovie, setAnimationGenreMovie] = useState([]);
    const [FantasyGenreMovie, setFantasyGenreMovie] = useState([]);
    const [sfGenreMovie, setSFGenreMovie] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const actionMovie = await getMovieByGenreFromTMDB(28);
            const comedyMovie = await getMovieByGenreFromTMDB(35);
            const animationMovie = await getMovieByGenreFromTMDB(16);
            const fantasyMovie = await getMovieByGenreFromTMDB(14);
            const sfMovie = await getMovieByGenreFromTMDB(878);

            setactionGenreMovie(actionMovie.data);
            setComedyGenreMovie(comedyMovie.data);
            setAnimationGenreMovie(animationMovie.data);
            setFantasyGenreMovie(fantasyMovie.data);
            setSFGenreMovie(sfMovie.data);

            console.log(actionMovie);
        }
        fetchData()
    }, [])

    return (
        <AppLayout>
            <div style={{ height: '600px', background: 'gray' }}>
                <h2 style={{ textAlign: 'center' }}>MovieTalk</h2>
            </div>
            <div>
                <div>최신 영화</div>
                <div>
                    <div>액션 | Action</div>
                    { actionGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={actionGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>코미디 | Drama</div>
                    { comedyGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={comedyGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>애니메이션 | Mistery</div>
                    { AnimationGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={AnimationGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>판타지 | Comedy</div>
                    { FantasyGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={FantasyGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>공상과학 | Comedy</div>
                    { sfGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={sfGenreMovie} />
                        )
                    }
                </div>
                
                <div>
                    <div>무비톡에서 리뷰가 많은 영화</div>
                </div>
            </div>
        </AppLayout>
    )
}

export default Home;
