import React, { useState, useEffect } from 'react'
import { getMovieList } from '../api'
import axios from 'axios'
import AppLayout from '../components/AppLayout'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [moviePoster, setMoviePoster] = useState([])

    useEffect(() => {
        const fetchMovieList = async () => {
            const result = await getMovieList();
            const movieList = result.boxOfficeResult.dailyBoxOfficeList;
            console.log(movieList);
            setMovieList(movieList);
            for(let i=0; i<movieList.length; i++) {
                const movieName = movieList[i].movieNm;
                const config = {
                    params: { query: movieName, }
                }
                
                const getNaverMovie = () => axios.get('http://localhost:5000/naver/getNaverMovie', config)
                    .then((res) => {
                        return res.data
                    })
                    .catch((err) => {
                        console.log(err);
                    })
                const a = getNaverMovie()
                console.log(a);
            }
        }
        fetchMovieList();
    }, [])
    /*
    const getNaverMovie = async () => {
        try {
            const res = await axios.get('http://localhost:5000/naver/getNaverMovie', {
                params: {
                    query: movieName,
                },
            });
            if(res && res.status === 200) {
                return res.data.items
            }
        } catch (err) {
            console.log('error', err);
        }
    }
    const a = getNaverMovie()
    console.log(a); 
    */
    return (
        <AppLayout>
            <div>
            {
                movieList.map(item => (
                    <div key={item.rnum}>
                        <span>{item.rnum}</span>
                        <div>{item.movieNm}</div>
                        {/* <img src={moviePoster} /> */}
                    </div>

                ))
            }
            </div>
        </AppLayout>
    )
}

export default Home;
