import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import styled from 'styled-components';
import { IoHeartOutline, IoHeartSharp } from "react-icons/io5";

import { LIKE_REVIEW_REQUEST, UNLIKE_REVIEW_REQUEST } from '../../reducers/review';

const MovieTalkCardWrap = styled.div`
    width: 500px;
`

function MovieTalkCard({ review, id }) {
    const dispatch = useDispatch();

    const [LikeAction, setLikeAction] = useState(false);

    const liked = review.likers.find((v) => v._id === id);

    const onLike = (e) => {
        e.preventDefault();

        const body = {
            reviewId: review._id,
            liker: id,
        };

        dispatch({
            type: LIKE_REVIEW_REQUEST,
            data: body, 
        });
        setLikeAction(true);
    };

    const onUnLike = (e) => {
        e.preventDefault();

        const body = {
            reviewId: review._id,
            disLiker: id,
        };

        dispatch({
            type: UNLIKE_REVIEW_REQUEST,
            data: body, 
        });
        setLikeAction(false);
    };


    return (
        <MovieTalkCardWrap>
            <img src={review.image} style={{ width: '200px' }} />
            <div>
                <div>작성자 : {review.name}</div>
                <div>{review.rating}</div>
                <div>{review.text}</div>
                {
                    !liked ?
                    <IoHeartOutline onClick={onLike} style={{ fontSize: '25px' }} />
                    :
                    <IoHeartSharp onClick={onUnLike} style={{ fontSize: '25px' }} />
                }
            </div>
        </MovieTalkCardWrap>
    )
};


export default MovieTalkCard;
