import React from 'react'
import Slider from "react-slick";
import styled from 'styled-components'

const MovieItem = styled.li`
    border: 1px solid red;
`

function MovieSlider({ movieInfomation }) {
    const settings = {
        dots: true, 
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 3,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:""
    };

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <MovieItem key={item.id} style={{ display: 'inline-block' }}>
                        <img src={item.poster} style={{ width: '50%' }} />
                        <div>{item.name}</div>
                        <button>예매</button>
                    </MovieItem>
                ))
            }
        </Slider>

    )
}

export default MovieSlider
