import axios from 'axios';
import { all, fork, call, put, takeLatest } from 'redux-saga/effects';
import {
    ADD_REVIEW_REQUEST,
    ADD_REVIEW_SUCCESS,
    ADD_REVIEW_FAILURE,
    LOAD_MY_REVIEWS_REQUEST,
    LOAD_MY_REVIEWS_SUCCESS,
    LOAD_MY_REVIEWS_FAILURE,
} from '../reducers/review';

// api
function addReviewAPI(data) {
    return axios.post('/review', data)
}

function loadReviewsAPI(data) {
    return axios.post('/reviews', data)
}

// action - reducers
function* addReview(action) {
    try {
        yield call(addReviewAPI, action.data)
        console.log('saga ADD_REVIEW');  
        yield put({
            type: ADD_REVIEW_SUCCESS,
        })
    } catch(err) {
        yield put({
            type: ADD_REVIEW_FAILURE,
            data: err.response.data
        })
    }
}

function* loadReviews(action) {
    try {
        yield call(loadReviewsAPI, action.data)
        console.log('saga ADD_REVIEW');  
        yield put({
            type: LOAD_MY_REVIEWS_SUCCESS,
        })
    } catch(err) {
        yield put({
            type: LOAD_MY_REVIEWS_FAILURE,
            data: err.response.data
        })
    }
}


// start function
function* watchReview() {
    yield takeLatest(ADD_REVIEW_REQUEST, loadReviews);
}

function* watchReviews() {
    yield takeLatest(LOAD_MY_REVIEWS_REQUEST, loadReviews);
}


export default function* reviewSaga() {
    yield all([
      fork(watchReview),
      fork(watchReviews),
    ]);
}
