import Link from 'next/link';
import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'

function MovieInfo ({ shows }) {
    const router = useRouter();
    const { id } = router.query;
    // console.log(movieList);

    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths({ id }) {
	const res = await getMovieSeqFromKMDB(id);
	const list = await res.json();
	const paths = list.map((list) => ({
		params: { id: list.movieSeq.toString() },
	}));
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ id }) {
	const res = await getMovieSeqFromKMDB(id);
	const list = await res.json();
	return {
		props: { shows: list },
	};
}

export default MovieInfo;
