import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { imageURL } from '../../config/config';

const ModalWrap = styled.div`
    filter: blur(2px);
`

const Modal = styled.div`
    width: 500px;
    height: 700px;
    background: #2A2A28;
`

function ReviewModal({ movie }) {
    const [Review, setReview] = useState("");

    const onReviewHandler = (e) => {
        setReview(e.currentTarget.value);
    };

    const handleSubmitReview = (e) => {
        e.preventDefault();
        const body = {
            writer: me._id,
            image: `${imageURL}/original${movieDetail.poster_path}`,
            name: me.name,
            rating: 5,
            text: Review,
            likes: 0,
            movieId: id,
        };
        dispatch(addReviewRequestAction(body));
        Router.push(`/myPage/${me._id}`);
    };

    return (
        <ModalWrap>
            <Modal>
                <div style={{ width: '50%' }}>
                    <div>
                        <img src={`${imageURL}/w200/${movie.poster_path}`} style={{ borderRadius: '5px' }} />
                    </div>
                    <div>{movie.title}({movie.release_date.slice(0, 4)})</div>
                </div>
                <form onSubmit={handleSubmitReview} style={{ width: '50%' }}>
                    <textarea
                        placeholder="영화 리뷰를 적어주세요."
                        onChange={onReviewHandler}
                        style={{ width: '500px', height: '500px' }}
                    />
                    <button type="submit">제출</button>
                </form>
            </Modal>
        </ModalWrap>
    )
};

export default ReviewModal;
