import Link from 'next/link';
import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'

function MovieInfo ({ shows }) {

    // console.log(movieList);

    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths({ params }) {
	const res = await fetch(`http://api.koreafilm.or.kr/openapi-data2/wisenut/search_api/search_json2.jsp?collection=kmdb_new2&movieSeq=${params}&ServiceKey=QXYS65CA4DKL68FJYGTF`)
	const list = await res.json();

	const paths = list.map((list) => ({
		params: { id: list.movieSeq.toString() },
	}));
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ params }) {
	const res = await fetch(`http://api.koreafilm.or.kr/openapi-data2/wisenut/search_api/search_json2.jsp?collection=kmdb_new2&movieSeq=${params}&ServiceKey=QXYS65CA4DKL68FJYGTF/${params.id}`);
	const list = await res.json();
	return {
		props: { shows: list },
	};
}

export default MovieInfo;
