import React from 'react'
import styled from 'styled-components';

const ReviewCardWrap = styled.div`
    border: 1px solid gray;
    width: 45%;
    padding: 20px;
    border-radius: 3px;
    box-sizing: border-box;
    display: inline-block;
    position: relative;
    background: ${props => props.src || "blue"};

    &:after {
        content: "";
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        border-radius: 3px;
        background: gray;
        opacity: 0.6;
        z-index: 0;
    }

    & > div {
        position: relative;
        z-index: 1;
    }

    & > button {
        margin-top: 25px;
        position: relative;
        z-index: 1;
    }

    & > div + div {
        margin-top: 15px;
    }
`
// background: linear-gradient(to right, rgba(40,40,60,1) 0%,rgba(40,0,60,0) 90%);
// background-blend-mode: multiply;
// will-change: transform;
function ReviewCard({ myReview }) {
    return (
        <ReviewCardWrap style={{ background: `url(${myReview.iamge})`, backgroundSize: 'cover', backgroundPosition: 'center center' }}>
            <div style={{ fontSize: '24px' }}>제목</div>
            <div>{myReview.rating}★★★★★</div>
            <div>{myReview.text}</div>
            <button>더보기</button>
        </ReviewCardWrap>
    )
}

export default ReviewCard
