import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { getMovieList } from '../api'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [moviePoster, setMoviePoster] = useState([])
    /*
    useEffect(() => {
        const fetchMovieList = async () => {
            const result = await getMovieList();
            const moviePosters = result.boxOfficeResult.dailyBoxOfficeList;
            const movieNm = moviePosters[0].movieNm
            console.log(movieNm);
        }
        fetchMovieList();
    }, [])
    /*
    moviePosters.forEach(poster => {
        const fetchMoviePoster = async () => {
            const movieNm = poster.movieNm;
            const returnValue = await getNaverMovie(movieNm);
            // const posterUrl = returnValue.results[0].poster_path;
            // setMoviePoster(`http://image.tmdb.org/t/p/w300${posterUrl}`)
        }
        fetchMoviePoster();
    });
    */
    const movieNm = '미나리'
    const getNaverMovie = async () => {
        try {
            const res = await axios.get('/api/naver/getNaverMovie', {
                params: {
                    query: movieNm,
                },
            });
            console.log(res);
            if(res && res.status === 200) {
                const { data } = res;
                console.log(data);
            }
        } catch (e) {
            console.log('error', e);
        }
    }

    return (
        <div>
            {/* {
                movieList.map(item => (
                    <div key={item.rnum}>{item.movieNm}</div>
                ))
            } */}
            <img src={moviePoster} />
            <button onClick={getNaverMovie}>클릭</button>
        </div>
    )
}

export default Home;
