export const initialState = {
    loadReviewsDone: false,
    loadReviewsError: null,
    reviews: [],
};

export const LOAD_REVIEWS_REQUEST = 'LOAD_REVIEWS_REQUEST';
export const LOAD_REVIEWS_SUCCESS = 'LOAD_REVIEWS_SUCCESS';
export const LOAD_REVIEWS_FAILURE = 'LOAD_REVIEWS_FAILURE';


const reducer = (state=initialState, action) => {
    switch (action.type) {
        case LOAD_REVIEWS_REQUEST:
            console.log('reducer Review');
            return state = {
                ...state,
                loadReviewsDone: false,
            }
        case LOAD_REVIEWS_SUCCESS:
            console.log('reducer LOAD_REVIEWS_SUCCESS');
            return state = {
                ...state,
                loadReviewsDone: true,
                // reviews: [ ...state.reviews, action.data.comment ]
            }
        case LOAD_REVIEWS_FAILURE:
            console.log('reducer LOAD_REVIEWS_FAILURE');
            return state = {
                ...state,
                loadReviewsDone: false,
            }

        default:
            return state;
    }
};

export default reducer;
