import React, { useState, useEffect } from 'react'

import AppLayout from '../components/AppLayout'
import MovieInfoSlider from '../components/MovieInfoSlider';

import { getMovieGenreFromKMDB } from '../api';
import axios from 'axios';

function Home() {
    const [actionGenreMovie, setactionGenreMovie] = useState([]);
    const [comedyGenreMovie, setComedyGenreMovie] = useState([]);
    const [misteryGenreMovie, setMisteryGenreMovie] = useState([]);
    const [dramaGenreMovie, setDramaGenreMovie] = useState([]);
    const [Image, setImage] = useState([]);

    useEffect(() => {
        const fetch = async () => {
            /*
            const fetchData1 = await getMovieGenreFromKMDB("액션");
            const fetchData2 = await getMovieGenreFromKMDB("코메디");
            const fetchData3 = await getMovieGenreFromKMDB("미스터리");
            const fetchData4 = await getMovieGenreFromKMDB("드라마");
            setactionGenreMovie(fetchData1.Data[0].Result)
            setComedyGenreMovie(fetchData2.Data[0].Result)
            setMisteryGenreMovie(fetchData3.Data[0].Result)
            setDramaGenreMovie(fetchData4.Data[0].Result)
            */
            const data = await axios.get(`https://api.themoviedb.org/3/movie/now_playing?api_key=2b2e61361a6d20a07e728e56fc296333&language=ko&page=1®ion=KR`)
            console.log(data.data.results[0].poster_path);
            setImage(data.data.results[0].poster_path)
        }
        fetch()
    }, [])

    return (
        <AppLayout>
            <img src={`https://image.tmdb.org/t/p/w500/${Image}`} />
            <div style={{ height: '600px', background: 'gray' }}>
                <h2 style={{ textAlign: 'center' }}>MovieTalk</h2>
            </div>
            <div>
                <div>최신 영화</div>
                <div>
                    <div>액션 | Action</div>
                    { actionGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={actionGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>드라마 | Drama</div>
                    { dramaGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={dramaGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>미스터리 | Mistery</div>
                    { misteryGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={misteryGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>코미디 | Comedy</div>
                    { comedyGenreMovie &&
                        (
                            <MovieInfoSlider movieInfomation={comedyGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>무비톡에서 리뷰가 많은 영화</div>
                </div>
            </div>
        </AppLayout>
    )
}

export default Home;
