import React from 'react'
import { useSelector } from 'react-redux';
import Link from 'next/link';
import styled from 'styled-components';

import { Profile } from './Sections/Profile';
import { TopMenu } from './Sections/TopMenu';
import BottomMenu from './Sections/BottomMenu';

const NavBar = styled.div`
    width: 200px;
    height: 100vh;
    padding: 3rem 2rem;
    box-sizing: border-box;
    background: black;
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    z-index: 99999;
    transition: 1s;

    @media screen and (max-width: 900px) {
        left: -200px;
    }
`

const HomeLink = styled.a`
    color: red;
    display: block;
    font-size: 1.6rem;
    cursor: pointer;
`

function Nav() {
    const { me } = useSelector((state) => state.user);

    if(me && me.name) {
        return (
            <NavBar>
                <div>
                    <Link href="/">
                        <HomeLink>MovieTalk</HomeLink>
                    </Link>
                    <Profile me={me} />
                </div>
                <TopMenu />
                <BottomMenu me={me} />
            </NavBar>
        )
    } else {
        return (
            <NavBar>
                <div>
                    <Link href="/">
                        <HomeLink>MovieTalk</HomeLink>
                    </Link>
                    <Profile me={me} />
                </div>
                <TopMenu />
                <BottomMenu me={me} />
            </NavBar>
        ) 
    }
};

export default Nav;
