import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useRouter } from 'next/router';
import Router from 'next/router';
import styled from 'styled-components';
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../../store/configureStore';
import SubPageLayout from '../../components/layout/SubPageLayout';
import { imageURL } from '../../config/config';

const MovieReviewWrap = styled.div`
    width: 80%;
    margin: 0 auto;
    margin-top: 48px;
    display: flex;
`

function review(props) {
    const dispatch = useDispatch();
    const router = useRouter();

    const { id } = router.query;
    const { movieDetail } = props;

    return (
        <SubPageLayout>
            <MovieReviewWrap>
                <div style={{ width: '50%' }}>
                    <div>
                        <img src={`${imageURL}/w200/${movieDetail.poster_path}`} style={{ borderRadius: '5px' }} />
                    </div>
                    <div>{movieDetail.title}({movieDetail.release_date.slice(0, 4)})</div>
                </div>
                <form onSubmit={handleSubmitReview} style={{ width: '50%' }}>
                    <textarea
                        placeholder="영화 리뷰를 적어주세요."
                        onChange={onReviewHandler}
                        style={{ width: '500px', height: '500px' }}
                    />
                    <button type="submit">제출</button>
                </form>
            </MovieReviewWrap>
        </SubPageLayout>
    )
};

