import React from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../store/configureStore';
import SubPageLayout from '../components/layout/rating';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import { LOAD_ALL_REVIEWS_REQUEST } from '../reducers/review';

function movieTalk() {
    const { allReviews } = useSelector((state) => state.review);
    console.log(allReviews);

    return (
        <SubPageLayout>
            {
                allReviews && (
                    allReviews.map(review => (
                        <div>
                            <img src={review.iamge} style={{ width: '200px' }} />
                            <div>작성자 : {review.name}</div>
                            <div>{review.rating}</div>
                            <div>{review.text}</div>
                        </div>
                    ))
                )
            }
        </SubPageLayout>
    )
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
      axios.defaults.headers.Cookie = cookie;
    };
    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    context.store.dispatch({
        type: LOAD_ALL_REVIEWS_REQUEST,
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();
});


export default movieTalk;
