import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../store/configureStore';
import SubPageLayout from '../components/Layout/SubPageLayout';
import MovieInfoSlider from '../components/Slider/MovieInfoSlider';
import SearchForm from '../components/Form/SearchForm';

import { getPopularMovieFromTMDB, getMovieByGenreFromTMDB } from './api/api';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';

const MoiveSearchWrap = styled.div`
    position: absolute;
    top: 48px;
    left: 60%;
    transform: translate(-60%, 0);
`

const SubSlideWrap = styled.div`
    width: 70%;
    margin: 0 auto;
    position: absolute;
    top: 200px;
    left: 60%;
    transform: translate(-60%, 0);

    @media screen and (max-width: 900px) {
        width: 500px;
        width: 100%;
        left: 0px;
    }
`

const TitleOfList = styled.div`
    font-size: 20px;
    margin-bottom: 20px;
`

function movieSearch(props) {
    const { popularMovie, actionGenreMovie, fantasyGenreMovie, animationGenreMovie } = props;
    
    const [genre, setGenre] = useState('Action');

    const handleClickGenre = (e) => {
        setGenre(e.target.innerHTML);
    };

    return (
        <SubPageLayout>
            <MoiveSearchWrap>
                <SearchForm />
            </MoiveSearchWrap>
            <SubSlideWrap className="movie-list">
                <div>
                    <TitleOfList>최신 인기 영화</TitleOfList>
                    <div onClick={handleClickGenre}>
                        <div>Action</div>
                        <div>Fantasy</div>
                        <div>Animation</div>
                    </div>

                    <MovieInfoSlider movieInfomation={
                        genre === 'Action' ? animationGenreMovie :
                        genre === 'Fantasy' ? animationGenreMovie :
                        genre === 'Animation' ? animationGenreMovie : animationGenreMovie
                    } />
                </div>
            </SubSlideWrap>
        </SubPageLayout>
    );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
      axios.defaults.headers.Cookie = cookie; 
    };
    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();

    const popularMovie = await getPopularMovieFromTMDB();
    const actionGenreMovie = await getPopularMovieFromTMDB(28);
    const fantasyGenreMovie = await getPopularMovieFromTMDB(14);
    const animationGenreMovie = await getPopularMovieFromTMDB(16);
    
    return {
      props: { 
        popularMovie: popularMovie.results, 
        actionGenreMovie: actionGenreMovie.results, 
        fantasyGenreMovie: fantasyGenreMovie.results, 
        animationGenreMovie: animationGenreMovie.results, 
      },
    };
});

export default movieSearch;
