import React from 'react'
import Slider from "react-slick";
import styled from 'styled-components'

const MovieItem = styled.li`
`

function MovieSlider({ movieInfomation }) {
    const settings = {
        dots: true, 
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 4,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:"wrap-slider",
        initialSlide: 0,
        responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 900,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <MovieItem key={item.id} style={{ display: 'inline-block' }}>
                        <img src={item.poster} style={{ width: '100%', height: '300px', verticalAlign: 'top' }} />
                        <button className="btn ticketing-btn">예매</button>
                    </MovieItem>
                ))
            }
        </Slider>

    )
}

export default MovieSlider
