export const initialState = {
    isLoggedIn: false,
    me: null
};

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const loginRequestAction = (data) => ({
    type: LOG_IN_REQUEST,
    payload: data,
});

const reducer = (state=initialState, action) => {
    switch (action.type) {
        case LOG_IN_REQUEST:
            console.log('reducer login');
            return state = {
                ...state,
                isLoggedIn: true,
            }
        case LOG_IN_SUCCESS:
            return state = {
                ...state,
                isLoggedIn: true,
                me: { ...action.payload, nickname: 'meme' }
            }
        case LOG_IN_FAILURE:
            return state = {
                ...state,
                isLoggedIn: false,
            }
        default:
            return state;
    }
};

export default reducer;
