import React from 'react';
import styled from 'styled-components';

const CategoryBtn = styled.button`
    width: 80px;
    border: none;
    padding: 8px 0;
    border-radius: 15px;
    color: white;
    background:
        ${props => props.genre === 'Action' ? 'rgba(255, 0, 0, 0.8);' :
                   props.genre === 'Fantasy' ? 'rgba(204, 32, 32, 0.637)' :
                   props.genre === 'Animation' ? 'rgba(204, 32, 32, 0.637)' : 'none'
        };

    box-sizing: border-box;

    &:hover {
        background: rgba(255, 0, 0, 0.8);
    }
`

function CategoryBotton({ genre, isSelected }) {
    console.log();
    return (
        <CategoryBtn
            genre={genre}
            isSelected={isSelected}
        >
            {genre}
        </CategoryBtn>
    )
};

export default CategoryBotton;
