import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { imageURL } from '../../config/config';

const ModalWrap = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    backdrop-filter: blur(3px);
    z-index: 999999;
`

const Modal = styled.div`
    width: 600px;
    height: 600px;
    border-radius: 5px;
    padding: 20px;
    background-color: #2A2A28;
    box-sizing: border-box;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
`

const CloseBtn = styled.button`
    position: absolute;
    top: 20px;
    right: 20px;
`

const SubmitBtn = styled.button`
    position: absolute;
    bottom: 20px;
    right: 20px;
`

function ReviewModal({ movie, closeModal, handleChangeText, handleSubmitReview }) {
    return (
        <ModalWrap>
            <Modal>
                <div style={{ fontSize: '25px', marginBottom: '20px' }}>Review</div>
                <div style={{ display: 'flex' }}>
                    <div style={{ width: '50%' }}>
                        <div>
                            <img src={`${imageURL}/w300/${movie.poster_path}`} style={{ borderRadius: '5px', width: '260px' }} />
                        </div>
                        <div style={{ width: '260px' }}>
                            {movie.title}({movie.release_date.slice(0, 4)})
                        </div>
                    </div>
                    <form onSubmit={handleSubmitReview} style={{ width: '50%' }}>
                        <textarea
                            placeholder="영화 리뷰를 적어주세요."
                            onChange={handleChangeText}
                            style={{ width: '100%', height: '100%' }}
                        />
                        <SubmitBtn type="submit">제출</SubmitBtn>
                    </form>
                </div>
                <CloseBtn onClick={closeModal}>닫기</CloseBtn>
            </Modal>
        </ModalWrap>
    )
};

export default ReviewModal;
