export const initialState = {
    loadMovieFromTMDBDone: false,
    loadMovieFromTMDBError: null,
    movieSearchResult: [],
    pageNum: 0,
};

export const LOAD_MOVIE_FROM_TMDB_REQUEST = 'LOAD_MOVIE_FROM_TMDB_REQUEST';
export const LOAD_MOVIE_FROM_TMDB_SUCCESS = 'LOAD_MOVIE_FROM_TMDB_SUCCESS';
export const LOAD_MOVIE_FROM_TMDB_FAILURE = 'LOAD_MOVIE_FROM_TMDB_FAILURE';


const reducer = (state=initialState, action) => {
    switch (action.type) {
        case LOAD_MOVIE_FROM_TMDB_REQUEST:
            console.log('reducer LOAD_MOVIE_FROM_TMDB - REQUEST');
            return state = {
                ...state,
                loadMovieFromTMDBDone: false,
                loadMovieFromTMDBError: null,
            }
        case LOAD_MOVIE_FROM_TMDB_SUCCESS:
            console.log('reducer LOAD_MOVIE_FROM_TMDB - SUCCESS');
            return state = {
                ...state,
                loadMovieFromTMDBDone: true,
                movieSearchResult: [ ...state.movieSearchResult, ...action.data ],
            }
        case LOAD_MOVIE_FROM_TMDB_FAILURE:
            return state = {
                ...state,
                loadMovieFromTMDBError: action.error,
            }

        default:
            return state;
    }
};

export default reducer;
