import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'
import axios from 'axios';

export default function MovieInfo (props) {
    const router = useRouter();
    const { id } = router.query;

    console.log(props);

    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths() {
    // const movieSeq = "50568";
    // const res = await getMovieFromKMDB(movieSeq);
    // const result = res.Data[0].Result;

    const paths = [
        { params: { id: "50568" } }
    ]
    
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps() {
	const res = await axios.get(`http://api.koreafilm.or.kr/openapi-data2/wisenut/search_api/search_json2.jsp?collection=kmdb_new2&movieSeq=50568&ServiceKey=QXYS65CA4DKL68FJYGTF`).data

	return {
		props: { res }
	};
}
