import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';

import Logo from './Sections/Logo';
import Profile from './Sections/Profile';
import TopMenu from './Sections/TopMenu';
import BottomMenu from './Sections/BottomMenu';
import HambergerBtn from './Sections/HambergerBtn';


const NavBar = styled.div`
    width: 200px;
    height: 100vh;
    padding: 48px 32px;
    box-sizing: border-box;
    background: black;
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    z-index: 99999;
    transition: 1s;

    @media screen and (max-width: 900px) {
        left: ${isOpened => isOpened ? '-200px' : '0px'};
    }
`

const NavBtn = styled.div`
    width: 40px;
    height: 40px;
    background: red;
    position: fixed;
    top: 0;
    left: 0;
    
    @media screen and (max-width: 900px) {
        
    }
`

function Nav() {
    const { me } = useSelector((state) => state.user);

    const [isOpened, setIsOpened] = useState(false);
    console.log(isOpened);
    const closeMenu = () => {
        console.log("close");
        setIsOpened(false);
    };

    const openMenu = () => {
        console.log("open");
        setIsOpened(true);
    };

    if(me && me.name) {
        return (
            <NavBar>
                <NavBtn isOpened={isOpened} onClick={isOpened ? closeMenu : openMenu} />
                <div>
                    <Logo />
                    <Profile me={me} />
                </div>
                <TopMenu />
                <BottomMenu me={me} />
            </NavBar>
        )
    } else {
        return (
            <NavBar>
                <NavBtn isOpened={isOpened} onClick={closeMenu} />
                <div>
                    <Logo />
                    <Profile me={me} />
                </div>
                <TopMenu />
                <BottomMenu me={me} />
            </NavBar>
        ) 
    }
};

export default Nav;
