import React, { useState } from 'react';
import styled from 'styled-components';

import AppLayout from '../components/layout/AppLayout';
import { IoSearchSharp } from "react-icons/io5";

import { getMovieFromTMDB } from './api/api';

const SearchInput = styled.input`
    width: 250px;
    height: 50px;
    margin: 0 auto;
    text-align: center;
    border-radius: 2rem;
    border: none;
`

function movieSearch() {
    const [inputValue, setInputValue] = useState("");

    const handleInputValue = (e) => {
        setInputValue(e.target.value)
    };

    const handleSubmitValue = (e) => {
        e.preventDefault();

        const fetchMovieList = async () => {
            const fetchData = await getMovieFromTMDB(inputValue);
            console.log(fetchData.results);
            if(!fetchData.results) {
                setMovieList(undefined);
                setIsEmptyMovieList(true);
            } else {
                setMovieList(fetchData.results);
                setIsEmptyMovieList(false);
            }
        }
        fetchMovieList();
    };
    
    return (
        <AppLayout>
            <form
                onSubmit={handleSubmitValue}
                style={{ textAlign: 'center', position: 'relative', top: '2.5rem', left: '2rem' }}
            >
                <SearchInput
                    placeholder="영화명을 입력해주세요."
                    onChange={handleInputValue}
                />
                <button type="submit" className="btn search-btn">
                    <IoSearchSharp style={{ color: 'white', fontSize: '1.5rem' }} />
                </button>
            </form>
        </AppLayout>
    );
};

export default movieSearch;
