import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import styled from 'styled-components';
import { IoHeartOutline, IoHeartSharp } from "react-icons/io5";

import { LIKE_REVIEW_REQUEST, UNLIKE_REVIEW_REQUEST } from '../../reducers/review';

const MovieTalkCardWrap = styled.div`
    width: 500px;
`

function MovieTalkCard({ review }) {
    const dispatch = useDispatch();
    const id = useSelector((state) => state.user.me?.id);

    const onUnLike = () => {
        dispatch({
            type: UNLIKE_REVIEW_REQUEST,
            data: review._id,
        });
    };

    const onLike = () => {
        dispatch({
            type: LIKE_REVIEW_REQUEST,
            data: review._id,
        });
    };

    return (
        <MovieTalkCardWrap>
            <img src={review.iamge} style={{ width: '200px' }} />
            <div>
                <div>작성자 : {review.name}</div>
                <div>{review.rating}</div>
                <div>{review.text}</div>
                <IoHeartOutline
                    onClick={onUnLike}
                    style={{ fontSize: '25px' }}
                />
                <IoHeartSharp
                    onClick={onLike}
                    style={{ fontSize: '25px' }}
                />
            </div>
        </MovieTalkCardWrap>
    )
};

export default MovieTalkCard;
