import React, { useState } from 'react';
import { useRouter } from "next/router";

import MovieInfoData from '../../json/movieInfo.json'
import AppLayout from '../../components/AppLayout'

function MovieInfo ({ movieList }) {
    const router = useRouter();
    const { id } = router.query;

    const movieInfoItem = movieList.filter(item => item.movieSeq.toString() === id)[0]

    return (
        <AppLayout>
            <div>{movieInfoItem.id}</div>
            <div>
                <div>{movieInfoItem.name}</div>
            </div>
            <img src={movieInfoItem.poster} />
        </AppLayout>
    )
}

export default MovieInfo;
