import React, { useState, useEffect } from 'react'
import { getMovieList } from '../api'
import axios from 'axios'
import AppLayout from '../components/AppLayout'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [moviePoster, setMoviePoster] = useState([])

    useEffect(() => {
        const fetchMovieList = async () => {
            const result = await getMovieList();
            const movieList = result.boxOfficeResult.dailyBoxOfficeList;
            const movieName = movieNameArray(movieList)
            console.log(movieName);
            setMovieList(movieList);
            const a = () => {
                const arr = [];
                for(let i=0; i<movieList.length; i++) {
                    const movieName = movieList[i].movieNm;
                    const arr = getNaverMovie(movieName);
                }
                return arr
            }
            const c= a()
            console.log(c);

        }
        fetchMovieList();
    }, [])

    const movieNameArray = (list) => {
        var arr = [];

        for(let i=0; i<list.length; i++) {
            arr.push(list[i].movieNm)
        }
        return arr;
    }

    const getNaverMovie = async (name) => {
        try {
            const res = await axios.get('http://localhost:5000/naver/getNaverMovie', {
                params: {
                    query: name,
                },
            });
            if(res && res.status === 200) {
                return res.data
            }
        } catch (err) {
            console.log('error', err);
        }
    }

    return (
        <AppLayout>
            <div>
            {
                movieList.map(item => (
                    <div key={item.rnum}>
                        <span>{item.rnum}</span>
                        <div>{item.movieNm}</div>
                        {/* <img src={moviePoster} /> */}
                    </div>

                ))
            }
            </div>
        </AppLayout>
    )
}

export default Home;
