import React from 'react';
import styled from 'styled-components';

const CategoryBtn = styled.button`
    width: 80px;
    border: none;
    padding: 8px 0;
    border-radius: 15px;
    color: white;
    background: rgba(255, 0, 0, 0.8);
    // opacity: ${props => props.isSelected ? '.5' : '0'};
    box-sizing: border-box;

    &:hover {
        background: rgba(255, 0, 0, 0.8);
    }
`

function CategoryBotton({ genre, isSelected }) {
    console.log(genre);
    return (
        <CategoryBtn isSelected={isSelected}>
            {genre}
        </CategoryBtn>
    )
};

export default CategoryBotton;
