import React, { useState, useEffect } from 'react'

import AppLayout from '../components/AppLayout'
import MovieSlider from '../components/MovieStllSlider';

import { getMovieGenreFromKMDB } from '../api';

function Home() {
    const [actionGenreMovie, setactionGenreMovie] = useState([]);
    const [comedyGenreMovie, setComedyGenreMovie] = useState([]);
    const [misteryGenreMovie, setMisteryGenreMovie] = useState([]);
    const [dramaGenreMovie, setDramaGenreMovie] = useState([]);

    useEffect(() => {
        const fetch = async () => {
            const fetchData1 = await getMovieGenreFromKMDB("액션");
            const fetchData2 = await getMovieGenreFromKMDB("코메디");
            const fetchData3 = await getMovieGenreFromKMDB("미스터리");
            const fetchData4 = await getMovieGenreFromKMDB("드라마");
            setactionGenreMovie(fetchData1.Data[0].Result)
            setComedyGenreMovie(fetchData2.Data[0].Result)
            setMisteryGenreMovie(fetchData3.Data[0].Result)
            setDramaGenreMovie(fetchData4.Data[0].Result)
        }
        fetch()
    }, [])

    return (
        <AppLayout>
            <div style={{ height: '600px', background: 'gray' }}>
                <h2 style={{ textAlign: 'center' }}>MovieTalk</h2>
            </div>
            <div>
                <div>최신 영화</div>
                <div>
                    <div>액션 | Action</div>
                    { actionGenreMovie &&
                        (
                            <MovieSlider movieInfomation={actionGenreMovie} />
                        )
                    }
                </div>
                <div>
                    <div>드라마 | Drama</div>
                </div>
                <div>
                    <div>미스터리 | Mistery</div>
                </div>
                <div>
                    <div>코미디 | Comedy</div>
                </div>
                <div>
                    <div>무비톡에서 리뷰가 많은 영화</div>
                </div>
            </div>
        </AppLayout>
    )
}

export default Home;
