import Link from 'next/link';
import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieFromKMDB } from '../../api'

function MovieInfo ({ shows }) {
    const router = useRouter();
    const { id } = router.query;
    console.log(id);
    console.log(shows);


    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths() {
    // const movieSeq = "50568";
    // const res = await getMovieFromKMDB(movieSeq);
    // const result = res.Data[0].Result;

    const paths = [
        { params: { id: "50568" } }
    ]
    
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ params }) {
	const res = await getMovieFromKMDB(params.id);
    const result = res.Data[0].Result;

	return {
		props: { 
            shows: result
        },
	};
}

export default MovieInfo;
