import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { imageURL } from '../../config/config';

const ModalWrap = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    backdrop-filter: blur(3px);
    z-index: 999999;
`

const Modal = styled.div`
    width: 600px;
    height: 600px;
    background: #2A2A28;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    border-radius: 5px;
`

function ReviewModal({ movie, isModalOpen, closeModal }) {
    const [text, setText] = useState("");

    const handleChangeText = (e) => {
        setText(e.currentTarget.value);
    };

    const handleSubmitReview = (e) => {
        e.preventDefault();
        const body = {
            writer: me._id,
            image: `${imageURL}/original${movieDetail.poster_path}`,
            name: me.name,
            rating: 5,
            text: text,
            likes: 0,
            movieId: id,
        };
        dispatch(addReviewRequestAction(body));
        Router.push(`/myPage/${me._id}`);
    };

    return (
        <ModalWrap>
            <Modal>
                <div style={{ fontSize: '25px' }}>Review</div>
                <div style={{ width: '50%' }}>
                    <div>
                        <img src={`${imageURL}/w200/${movie.poster_path}`} style={{ borderRadius: '5px' }} />
                    </div>
                    <div>{movie.title}({movie.release_date.slice(0, 4)})</div>
                </div>
                <form onSubmit={handleSubmitReview} style={{ width: '50%' }}>
                    <textarea
                        placeholder="영화 리뷰를 적어주세요."
                        onChange={handleChangeText}
                        style={{ width: '500px' }}
                    />
                    <button type="submit">제출</button>
                </form>
                <button onClick={closeModal}>닫기</button>
            </Modal>
        </ModalWrap>
    )
};

export default ReviewModal;
