export async function getStaticPaths() {
    const movieSeq = "50568";
    const res = await getMovieSeqFromKMDB(movieSeq);
    const result = res.Data[0].Result;

    const paths = result.map((item) => ({
        params: { id: item.movieSeq },
    }))

	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ params }) {
	const res = await getMovieSeqFromKMDB(params.id);
    const result = res.Data[0].Result;

	return {
		props: { shows: result },
	};
}

export default MovieInfo;
