import Link from 'next/link';
import React from 'react';
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'

function MovieInfo ({ shows }) {
    const router = useRouter();
    const { id } = router.query;
    console.log(id);
    console.log(shows);
    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths() {
    const movieSeq = "50568";
    const res = await getMovieSeqFromKMDB(movieSeq);
    const result = res.Data[0].Result;

    const paths = result.map((item) => ({
        params: { id: item.movieSeq },
    }))
    console.log(paths);
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ params }) {
	const res = await getMovieSeqFromKMDB(params.id);
    const result = res.Data[0].Result;

	return {
		props: { 
            shows: result
        },
	};
}

export default MovieInfo;
