import React, { useState } from 'react';
import styled from 'styled-components';
import Router from "next/router";
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../store/configureStore';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import { getpopularMovieFromTMDB } from './api/api';
import MainPageLayout from '../components/Layout/MainPageLayout'
import SearchMovieBtn from '../components/Botton/SearchMovieBtn';
import MovieMainSlider from '../components/Slider/MovieMainSlider';

const MainSlideWrap = styled.div`
    height: 100vh;
    margin-left: 200px;
    transition: 1s;
    
    & > div:nth-child(1) {
        position: absolute;
        top: 50%;
        left: 60%;
        transform: translate(-60%, -50%);
        z-index: 1;
    }

    @media screen and (max-width: 900px) {
        margin-left: 0;

        & > div:nth-child(1) {
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }
    }
`

const SiteName = styled.div`
    font-size: 30px;
    color: red;
    display: none;
    position: absolute;
    top: -60px;
    left: 40px;
    z-index: 1;

    @media screen and (max-width: 900px) {
        display: block;
    }
`

const TitleOfList = styled.div`
    font-size: 14px;
    color: red;
    position: absolute;
    bottom: 20px;
    left: 0;
    z-index: 1;
`

const SearchInput = styled.input`
    width: 500px;
    height: 70px;
    margin: 0 auto;
    text-align: center;
    border-radius: 32px;
    border: none;
    background: rgba(133, 133, 133, 0.479);
    color: white;
    position: relative;
        
    &::placeholder {
        color: black;
    }

    @media screen and (max-width: 900px) {
        width: 220px;
        height: 60px;

        &::placeholder {
            color: black;
            font-size: 12px;
        }
    }
`

const SearchForm = styled.div`
    text-align: center;

    @media screen and (max-width: 900px) {
    }
`



// const SubSlideWrap = styled.div`
//     width: 1320px;
//     margin: 0 auto;
//     position: absolute;
//     bottom: 32px;
//     left: 250px;
//     transition: 1s;

//     @media screen and (max-width: 900px) {
//         width: 500px;
//         width: 100%;
//         left: 0px;
//     }
// `

function Home(props) {
    const [inputValue, setInputValue] = useState("");
    
    const { popularMovie } = props;

    const handleInputValue = (e) => {
        setInputValue(e.target.value);
    };

    const handleSubmitValue = (e) => {
        e.preventDefault();
        Router.push(`/movieSearch/${inputValue}`);
    };

    return (
        <MainPageLayout>
            <MainSlideWrap className="main-content">
                <div>
                    <SiteName>MovieTalk</SiteName>
                    <SearchForm onSubmit={handleSubmitValue}>
                        <SearchInput
                            placeholder="Search movie!"
                            onChange={handleInputValue}
                        />
                        <SearchMovieBtn />
                    </SearchForm>
                    <TitleOfList>Popular movie ></TitleOfList>
                </div>
                <MovieMainSlider movieInfomation={popularMovie}></MovieMainSlider>
            </MainSlideWrap>

            {/* <SubSlideWrap className="movie-list">
                <div>
                    <TitleOfList>최신 인기 영화</TitleOfList>
                    { popularMovie &&
                        (
                            <MovieInfoSlider movieInfomation={popularMovie} />
                        )
                    }
                </div>
            </SubSlideWrap> */}
        </MainPageLayout>
    )
}

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
      axios.defaults.headers.Cookie = cookie;
    };

    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();

    const popularMovie = await getpopularMovieFromTMDB();
    
    return {
      props: { 
        popularMovie: popularMovie.results, 
      },
    };
});

export default Home;
