import React, { useState, useEffect } from 'react'

import AppLayout from '../components/AppLayout'
import MovieSlider from '../components/MovieSlider';

import { getMovieGenreFromKMDB } from '../api';

function Home() {
    useEffect(() => {
        const fetch = async () => {
            const fetchData1 = await getMovieGenreFromKMDB("액션");
            const fetchData2 = await getMovieGenreFromKMDB("코메디");
            const fetchData3 = await getMovieGenreFromKMDB("미스터리");
            const fetchData4 = await getMovieGenreFromKMDB("드라마");
            console.log(fetchData1.Data[0].Result);
            console.log(fetchData2.Data[0].Result);
            console.log(fetchData3.Data[0].Result);
            console.log(fetchData4.Data[0].Result);
        }
        fetch()
    }, [])

    return (
        <AppLayout>
            <div style={{ height: '600px', background: 'gray' }}>
                <h2 style={{ textAlign: 'center' }}>MovieTalk</h2>
            </div>
            <div>
                <div>최신 영화</div>
                <div>
                    <div>액션 | Action</div>
                    {/* { result[0].stlls &&
                        (
                            <MovieSlider movieInfomation={)} />
                        )
                    } */}
                </div>
                <div>
                    <div>드라마 | Drama</div>
                </div>
                <div>
                    <div>미스터리 | Mistery</div>
                </div>
                <div>
                    <div>코미디 | Comedy</div>
                </div>
                <div>
                    <div>무비톡에서 리뷰가 많은 영화</div>
                </div>
            </div>
        </AppLayout>
    )
}

export default Home;
