import Link from 'next/link';
import React from 'react';
import styled from 'styled-components'

import MovieInfoLayOut from '../../components/MovieInfoLayOut';
import MovieComment from '../../components/MovieComment'
import MovieStllSlider from '../../components/MovieStllSlider'
import { getMovieDetailFromTMDB } from '../api/api'

const MovieBasicInfo = styled.div`
    width: 80%;
    margin: 0 auto;
    display: flex;
    color: white;
`

const MovieDetailInfo = styled.div`
    width: 80%;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    color: white;
`

export default function MovieInfoPage (props) {
    const { result } = props;
    console.log(result);

    const joinArrayOfActors = (actors) => {
        const sliceArray = actors.slice(0, 5);
        const actorsNameArray = [];
        for(let i=0; i<sliceArray.length; i++) {
            actorsNameArray.push(sliceArray[i].actorNm)
        }
        const joinArrayToString = actorsNameArray.join(", ");
        return joinArrayToString;
    };

    const imageURL = 'https://image.tmdb.org/t/p/w300/'

    return (
        <MovieInfoLayOut>
            <div style={{ width: '90%', textAlign: 'right', margin: '0 auto 1rem' }}>
                <span style={{ color: 'white' }}>이 영화를 무비톡에 남기고 싶다면? </span>
                <Link href="/movieTalk">
                    <a>톡 작성하기</a>
                </Link>
            </div>
            <MovieBasicInfo>
                <div>
                    <img src={`${imageURL}${result.poster_path}`} style={{ borderRadius: '5px' }} />
                </div>
                <div style={{ marginLeft: '2rem' }}>
                    { result.title &&
                        (
                            <div style={{ fontSize: '2rem' }}>{result.title}</div>
                        )
                    }
                    {
                        result.original_title &&
                        (
                            <div style={{ fontSize: '1.5rem' }}>{result.original_title}</div>
                        )
                    }
                    {
                        result.release_date &&
                        (
                            <div>개봉일 : {result.release_date}</div>
                        )
                    }
                    {/* {
                        result.genre && result.runtime &&
                        (
                            <div>장르 : {result.genre} / {result.runtime}분</div>
                        )
                    } */}
                    {/* {
                        result.directors.director.directorNm &&
                        (
                            <div>감독 : {result.directors.director.directorNm}</div>
                        )
                    } */}
                    {/* {
                        result.actors.actor.actorNm &&
                        (
                            <div>배우 : {joinArrayOfActors(result.actors.actor)}</div>
                        )
                    } */}
                </div>
            </MovieBasicInfo>
            <MovieDetailInfo>
                {
                    result.overview &&
                    (
                        <div>
                            <div>줄거리</div>
                            <div>{result.overview}</div>
                        </div>
                    )
                }
                { result.video &&
                    (
                        <video />
                    )
                }
            </MovieDetailInfo>
            <div style={{ width: '80%', margin: '0 auto', border: '1px solid red' }}>
                <div>댓글</div>
                <div>
                    <MovieComment />
                </div>
            </div>
        </MovieInfoLayOut>
    )
};

export async function getServerSideProps({ query }) {
    const res = await getMovieDetailFromTMDB(query.id)
    const result = await res;

    return {
      props: { result },
    }
}
