import { getMovieSeqFromKMDB } from '../../api'

function MovieInfo ({ result }) {


    console.log(result);


    return (
        <AppLayout>
            <Link href="/movieTalk">
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths() {
    // const movieSeq = "50568";
    // const res = await getMovieFromKMDB(movieSeq);
    // const result = res.Data[0].Result;
    const router = useRouter();
    const { id } = router.query;
    console.log(id);
    const paths = [
        { params: { id: "50568" } }
    ]
    
	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps(context) {
	const res = await getMovieSeqFromKMDB(context.params.id);
    const result = res.Data[0].Result[0];

	return {
		props: { result }
	};
}

export default MovieInfo;
