import React, { useState, useEffect } from 'react'
import { getMovieList, getMoviePoster } from '../api'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [moviePoster, setMoviePoster] = useState('')

    useEffect(() => {
        const fetchMovieList = async () => {
            const result = await getMovieList();
            console.log(result.boxOfficeResult.dailyBoxOfficeList.movieNm);
            setMovieList(result.boxOfficeResult.dailyBoxOfficeList)
        }
        fetchMovieList();
        
        const fetchMoviePoster = async () => {
            const result = await getMoviePoster();
            const result1 = result.results[0].poster_path;

            setMoviePoster(`http://image.tmdb.org/t/p/w300${result1}`)
        }
        fetchMoviePoster();
    }, [])

    return (
        <div>
            {
                movieList.map(item => (
                    <div key={item.rnum}>{item.movieNm}</div>
                ))
            }
            <img src={moviePoster} />

        </div>
    )
}

export default Home;
