import axios from 'axios'
import { all, fork, call, put, takeEvery, delay } from 'redux-saga/effects' // saga의 effect


// api
function loginAPI(data) {
    return axios.post('/api/login', data)
}


// action - reducers
function* logIn(action) {
    // const result =  yield call(loginAPI, action.data)
    yield delay(1000);
    try {
        yield put({
            type: 'LOG_IN_SUCCESS',
            data: action.payload
        })
    } catch(err) {
        yield put({
            type: 'LOG_IN_FAILURE',
            data: err.response.data
        })
    }
}


// start function
function* watchLogIn() {
    yield takeEvery('LOGIN_IN_REQUEST', logIn)
}


export default function* userSaga() {
    yield all([
      fork(watchLogIn),
    ]);
}
