        return arr;
    }

    const getNaverMovie = async (name) => {
        try {
            const res = await axios.get('http://localhost:5000/naver/getNaverMovie', {
                params: {
                    query: name,
                },
            });
            if(res && res.status === 200) {
                console.log(res.data.items)
            }
        } catch (err) {
            console.log('error', err);
        }
    }

    return (
        <AppLayout>
            <ol>
            {
                movieList.map(item => (
                    <li key={item.rnum} style={{ listStyle: 'none' }}>
                        <span>{item.rnum}</span>
                        <div>{item.movieNm}</div>
                        {/* <img src={moviePoster} /> */}
                    </li>

                ))
            }
            </ol>
        </AppLayout>
    )
}

export default Home;
