import React from 'react'
import Slider from "react-slick";

function MovieInfoSlider({ movieInfomation }) {
    const settings = {
        dots: false, 
        infinite: true,
        slidesToShow: 7,
        slidesToScroll: 7,
        speed: 500,
        arrows: false,
        autoplay: false,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1300,
                settings: {
                  slidesToShow: 6,
                  slidesToScroll: 6,
                  infinite: true,
                  dots: true
                }
            },
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 5,
                slidesToScroll: 5,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 650,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    const havePosterPath = (posterPath) => {
        const imageURL = 'https://image.tmdb.org/t/p/w500/'

        if(!posterPath) {
            posterPath = "https://res.cloudinary.com/dvmqbovxh/image/upload/v1616866172/noposter_pcwcby.png";
            return posterPath;
        } else {
            return `${imageURL}${posterPath}`
        }
    };

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <div key={item.id}>
                        <img src={havePosterPath(item.poster_path)} style={{ width: '100%', borderRadius: '10px'}} />
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieInfoSlider
